@import url("https://fonts.googleapis.com/css?family=Open+Sans:400,400i,700");
*, *:before, *:after {
  box-sizing: border-box;
}

.wrapper {
  font-family: SF Mono, sans-serif;
  background-color: #0000ff;
  padding-top:100px;
  padding-bottom:100px;
}

.title-roadmap {
  color: #fff;
}

.wrapper {
  min-height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
}

.steps {
  max-width: 300px;
  margin: 0 auto;
}

.step {
  display: flex;
  position: relative;
}
.step:after {
  content: "";
  position: absolute;
  left: 15px;
  top: 32px;
  height: 0;
  width: 2px;
  background-color: #fff;
}
.step .info {
  margin: 8px 0 20px;
}
.step .title {
  font-size: 16px;
  font-weight: 600;
  margin: 0 0 8px;
  color: white;
}
.step .text {
  font-size: 12px;
  color: rgba(255, 255, 255, 0.7);
  padding-bottom: 0;
  margin: 0;
}
.step:not(:last-child):after {
  height: 75%;
}

.number {
  width: 32px;
  height: 32px;
  background-color: transparent;
  border-radius: 50%;
  border: 2px solid #fff;
  flex-shrink: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  color: white;
  font-size: 15px;
  font-weight: 600;
  margin-right: 14px;
}
.number.completed {
  background-color: #fff;
}
.number svg {
  width: 16px;
  height: 16px;
  object-fit: contain;
}
.number svg path {
  fill: white;
}
