.about-contract-wrapper {
  display: flex;
  flex-wrap: wrap;
  flex-direction: row;
  justify-content: center;
  background-color: #0f0f0f;
  min-height: 200px;
  padding-bottom: 200px;
}
.about-contract-container {
  width: 1000px;
  display: flex;
  justify-content: flex-start;
  flex-direction: row;
}
.about-contract-container > div {
  color: #fff;
  width: 50%;
}

.about-contract-wrapper h2 {
  font-weight: 400;
  font-size: 45px;
  color: white;
  margin-bottom: 0px;
  margin-top: 0px;
}
.about-contract-wrapper h3 {
  margin-bottom: 0px;
  margin-top: 0px;
}
.about-contract-wrapper h5 {
  margin-bottom: 0px;
  margin-top: 0px;
  opacity: 0.7;
}

.about-contract-container p {
  color: white;
  margin-bottom: 50px;
}

.number-figures {
  text-align: right;
  display: flex;
  width: 100%;
  flex-wrap: wrap;
  justify-content: flex-end;
}
.number-figures > div {
  width: 45%;
}
.number-figures div:nth-child(3) {
  width: 80%;
}

.demo-market {
  background-color: #0000ff;
  border-radius: 50px;
  min-height: 52px;
  padding-top: 20px;
  padding-bottom: 20px;
  padding-right: 30px;
  padding-left: 30px;
  color: #fff;
  transition: all ease-in 0.2s;
  border: #0000ff solid 1.5px;
  text-decoration: none;
}
.demo-market:hover {
  color: #fff;
  text-decoration: none;
}
.other {
  background-color: #56dfbf;
  border: #56dfbf solid 1.5px;
  margin-left: 20px;
}
.add-to-metamask {
  color: #9958c5;
  transition: all ease-in 0.5s;
  text-decoration: none;
}
.add-to-metamask:hover {
  border-bottom: #9958c5 dashed 1px;
  transition: all ease-in 0.5s;
  cursor: pointer;
}
