.hero-about-wrapper
{
    display:flex;
    justify-content:center;
    align-items:center;
    max-width:1200px;
    margin:auto;
    min-height:90vh;
}
.hero-about-wrapper > div
{
width:50%;
}
.hero-about-headline h1
{
    font-size: 50px;
    line-height: 69px;
    font-weight:normal;
margin-bottom:0px;
}
.hero-about-headline h3
{
    font-size: 22px;
    line-height: 34px;
    color: rgb(106, 106, 106);
    font-weight:normal;
}
figure{
  width: 50%;
  height: 400px;
  position: relative;
  background-color: #fff; 
  overflow: hidden;
}
video{
  object-fit: cover;
  position: absolute;
  top: 0px;
  left: 0;
  width: 100%;
  height: 100%;
}

figcaption{
  position: absolute;
  text-align: center;
  font-size: 1.2em;
  font-weight: normal;
  text-transform: uppercase;
  z-index: 2;
  width:200px;
  color:#000;
  border:solid 1px #fff;
  display:inline-block;
  background-color:white;
  padding:20px 30px;
left: 50%;
-webkit-transform: translateX(-50%);
transform: translateX(-50%);
top:38%;

}
.svg-clipped-text {
    -webkit-clip-path: url(#svgTextPath);
    clip-path: url(#svgTextPath);
}
